-- Create styles table
create table styles (
  id uuid default gen_random_uuid() primary key,
  style_id text unique not null,
  name text not null,
  description text not null,
  prompt_modifier text not null,
  icon text not null,
  color text not null,
  is_active boolean default true,
  sort_order int default 0,
  created_at timestamp with time zone default timezone('utc'::text, now()) not null
);

-- Enable RLS
alter table styles enable row level security;

-- Anyone can read active styles
create policy "Anyone can view active styles"
on styles for select
using (is_active = true);

-- Service role can manage styles
create policy "Service role can manage styles"
on styles for all
to service_role
using (true)
with check (true);

